import React from 'react';

const Hero = () => {
  return (
    <section className="hero-section">
      <div className="hero-container">
        {/* Left Side: Text + Button */}
        <div className="hero-content">
          <h1 className="hero-name">Tesfalem Ab</h1>
          <h2 className="hero-title">Full Stack Developer</h2>
          <p className="hero-intro">
            Passionate full-stack developer crafting modern web applications with React, Node.js, and cutting-edge technologies. 
            Turning ideas into seamless digital experiences.
          </p>
          <a href="#contact" className="hero-btn">
            Let's get started â†’
          </a>
        </div>

        {/* Right Side: Circular Avatar */}
        <div className="hero-avatar-container">
          <img 
            src="https://www.shutterstock.com/image-photo/smiling-man-formal-attire-poses-260nw-2604415501.jpg" 
            alt="Tesfalem Ab" 
            className="hero-avatar"
          />
          <div className="avatar-glow"></div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
