import React from 'react';

const Hero = () => {
  return (
    <section className="hero-section">
      <div className="hero-container">
        {/* Left Side: Text + Decorated Button */}
        <div className="hero-content">
          <h1 className="hero-name">Tesfalem Ab</h1>
          <h2 className="hero-title">Full Stack Developer</h2>
          <p className="hero-intro">
            Passionate full-stack developer crafting modern web applications with React, Node.js, MongoDB, and cutting-edge technologies. 
            Turning innovative ideas into seamless, high-performance digital experiences.
          </p>

          <a href="#contact" className="hero-btn">
            Let's get started <span className="btn-arrow">â†’</span>
          </a>
        </div>

        {/* Right Side: Your Profile Photo - More Space & Focus */}
        <div className="hero-avatar-container">
          <img 
            src="https://www.shutterstock.com/image-photo/portrait-shot-confident-male-african-600nw-2492802027.jpg" 
            alt="Tesfalem Ab" 
            className="hero-avatar"
          />
          <div className="avatar-glow"></div>
          <div className="avatar-ring"></div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
